    public static function init(): void
    {
        // Get custom platforms from config
        $config = Craft::$app->getConfig()->getConfigFromFile('social-media');
        if (isset($config['customPlatforms']) && is_array($config['customPlatforms'])) {
            self::$platforms = array_merge(self::$platforms, $config['customPlatforms']);
        }
    }

    public static function getLabel(string $platform): string
    {
        return self::$platforms[$platform]['label'] ?? $platform;
    }

    public static function getColor(string $platform): string
    {
        return self::$platforms[$platform]['color'] ?? '#000000';
    }

    public static function getIcon(string $platform): string
    {
        return self::$platforms[$platform]['icon'] ?? '';
    }
}